parameters.functionScript:
const inputString = Inputs.String || '';
if (!inputString) return;

try {
  const lowerCaseString = inputString.toLowerCase();
  const capitalizedString = lowerCaseString.charAt(0).toUpperCase() + lowerCaseString.slice(1);
  Outputs.CapitalizedString = capitalizedString;
  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

const inputString = Inputs.String || '';
if (!inputString) return;

try {
  const lowerCaseString = inputString.toLowerCase();
  const capitalizedString = lowerCaseString.charAt(0).toUpperCase() + lowerCaseString.slice(1);
  Outputs.CapitalizedString = capitalizedString;
  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

const dateInput = Inputs.DateInput || new Date();
if (!dateInput) return;

try {
  const date = new Date(dateInput);
  const now = new Date();

  const days = ['Sun', 'Mon', 'Tues', 'Wed', 'Thurs', 'Fri', 'Sat'];
  const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

  let formattedDate = '';

  if (date.getDate() === now.getDate() && date.getMonth() === now.getMonth() && date.getFullYear() === now.getFullYear()) {
    formattedDate = 'Today';
  } else if (date.getDate() === now.getDate() - 1 && date.getMonth() === now.getMonth() && date.getFullYear() === now.getFullYear()) {
    formattedDate = 'Yesterday';
  } else {
    formattedDate = `${days[date.getDay()]} ${date.getDate()} ${months[date.getMonth()]} ${date.getFullYear()}`;
  }

  const hours = date.getHours() < 10 ? '0' + date.getHours() : date.getHours();
  const minutes = date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes();

  formattedDate += ` at ${hours}:${minutes}`;

  Outputs.FormattedDate = formattedDate;
  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

Outputs.iconObject = {
    "class": "material-icons",
    "code": Inputs.iconName // in your case you would pass "thumbs_up" to this function
}

const oldName = Inputs.file.name; //change the file name here

const newName = oldName.replace(/[^_a-zA-Z0-9@. ~_-]/g, '')

const renamedFile = new File([Inputs.file], newName, {type: Inputs.file.type});

Outputs.renamedFile = renamedFile;
Outputs.newFileName = renamedFile.name;

Outputs.Done();

document.addEventListener('click', function(event) {
  const filtersPopunder = Inputs.This.getDOMElement();
  if (filtersPopunder && !filtersPopunder.contains(event.target)) {
    Outputs.ClickedOutside = true;

    // Reset Outputs.ClickedOutside to false after 500 milliseconds
    setTimeout(function() {
      Outputs.ClickedOutside = false;
    }, 500);
  }
});

if (!Inputs.itemClicked) return;

const itemClicked = Inputs.itemClicked;

Outputs.itemClicked = itemClicked;
Outputs.Success()

const clickedItem = Inputs.clickedItem;

if (!clickedItem) return;

switch (clickedItem) {
    case "profile":
    Outputs.toProfile();
    break;

    case "logIn":
    Outputs.toLogin();
    break;

    case "logOut":
    Outputs.toLogout();
    break;
}

