const allLabels = Inputs.allLabels;
const myLabels = Inputs.myLabels;

switch (Inputs.tab) {
    case "community":
    Outputs.array = allLabels;
    Outputs.totalCount = allLabels.length;
    break;

    case "mine":
    Outputs.array = myLabels;
    Outputs.totalCount = allLabels.length;
    break;
}

// Read input array and labelCode (if provided)
const inputArray = Inputs.InputArray || [];
const labelCodeInput = Inputs.LabelCode ? Inputs.LabelCode.trim().toLowerCase() : null;

try {
  // Filter the array by community being true
  let filteredArray = inputArray.filter(item => item.community === true);

  // If a labelCode is provided and not an empty string, filter by it (case-insensitive)
  if (labelCodeInput && labelCodeInput !== "") {
    filteredArray = filteredArray.filter(item => 
      item.labelCode && item.labelCode.toLowerCase() === labelCodeInput
    );
  }

  // Sort the filtered results by createdDate in descending order
  filteredArray.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

  // Set the filtered and sorted results to the output
  Outputs.FilteredResults = filteredArray;

  // Call Success signal
  Outputs.Success();
} catch (error) {
  // Log error and call Failure signal
  console.error("Error filtering and sorting data:", error);
  Outputs.Failure();
}

const inputArray = Inputs.InputArray || [];
const filterArray = Inputs.FilterArray || [];

// Check if input arrays are provided
if (!Array.isArray(inputArray) || !Array.isArray(filterArray)) {
  Outputs.Failure();
  return;
}

// Extract the values from the filter array
const filterValues = filterArray.map(item => item.Value);

// Filter the input array based on the theme field
const filteredResults = inputArray.filter(item => 
  item.theme && filterValues.includes(item.theme)
);

// Output the filtered results
Outputs.FilteredResults = filteredResults;
Outputs.ResultsCount = filteredResults.length;
Outputs.Success();

if (!Inputs.base64Image) return;

const base64Data = Inputs.base64Image.trim();

// Ensure the base64 string includes the "data:image/png;base64," prefix
let imgSrc = base64Data;
if (!base64Data.startsWith('data:image/png;base64,')) {
    imgSrc = 'data:image/png;base64,' + base64Data;
}

// Create a new image element
const img = new Image();
img.crossOrigin = 'Anonymous';  // Set the cross-origin attribute

// Set the src to the base64 image string
img.src = imgSrc;

img.onload = function() {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    canvas.width = img.width;
    canvas.height = img.height;
    ctx.drawImage(img, 0, 0);

    try {
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
        const data = imageData.data;

        // Function to check if a color is within the background range
        function isBackground(r, g, b) {
            const brightness = (r + g + b) / 3;
            const maxColorDiff = Math.max(Math.abs(r - g), Math.abs(r - b), Math.abs(g - b));

            // Adjust these values based on your specific images
            return brightness > 200 && maxColorDiff < 30;
        }

        // Flood fill algorithm
        function floodFill(x, y) {
            const stack = [[x, y]];
            const width = canvas.width;
            const height = canvas.height;

            while (stack.length) {
                const [x, y] = stack.pop();
                const index = (y * width + x) * 4;

                if (x < 0 || x >= width || y < 0 || y >= height) continue;
                if (data[index + 3] === 0) continue; // Already processed

                const r = data[index];
                const g = data[index + 1];
                const b = data[index + 2];

                if (!isBackground(r, g, b)) continue;

                // Set pixel to transparent
                data[index + 3] = 0;

                // Add neighboring pixels to stack
                stack.push([x + 1, y], [x - 1, y], [x, y + 1], [x, y - 1]);
            }
        }

        // Start flood fill from all four corners
        floodFill(0, 0);
        floodFill(canvas.width - 1, 0);
        floodFill(0, canvas.height - 1);
        floodFill(canvas.width - 1, canvas.height - 1);

        ctx.putImageData(imageData, 0, 0);

        const pngUrl = canvas.toDataURL('image/png');
        Outputs.pngUrl = pngUrl;
        Outputs.Success();
    } catch (e) {
        console.error('Error processing canvas:', e);
        Outputs.Failure();
    }
};

let result = '';
const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

for (let i = 0; i < 6; i++) {
result += characters.charAt(Math.floor(Math.random() * characters.length));
}

Outputs.code = result;
Outputs.Success();

document.addEventListener('click', function(event) {
  const filtersPopunder = Inputs.This.getDOMElement();
  if (filtersPopunder && !filtersPopunder.contains(event.target)) {
    Outputs.ClickedOutside = true;

    // Reset Outputs.ClickedOutside to false after 500 milliseconds
    setTimeout(function() {
      Outputs.ClickedOutside = false;
    }, 500);
  }
});

const fabricUniqueId = '--fabric-js--';
const fabricScriptUrl = `https://cdnjs.cloudflare.com/ajax/libs/fabric.js/5.3.0/fabric.min.js`;

function injectFabricScript(onload) {
    if (!document.querySelector(`#${fabricUniqueId}`)) {
        const script = document.createElement('script');
        script.id = fabricUniqueId;
        script.src = fabricScriptUrl;
        script.onload = () => {
            console.log('Fabric.js loaded successfully');
            onload();
        };
        script.onerror = () => {
            console.error('Failed to load Fabric.js');
        };
        document.head.appendChild(script);
    } else {
        console.log('Fabric.js already loaded');
        onload();
    }
}

