let canvas = null;

// Font loading function
function loadFont(fontName, fontUrl) {
    const font = new FontFace(fontName, `url(${fontUrl})`);
    font.load().then((loadedFont) => {
        document.fonts.add(loadedFont);
        console.log(`Font ${fontName} loaded successfully`);
        // Refresh the canvas to apply the new font
        if (canvas) {
            canvas.renderAll();
        }
    }).catch((error) => {
        console.error(`Error loading font ${fontName}:`, error);
    });
}

Script.Signals.Setup = function () {
    const element = Script.Inputs.This.getDOMElement();
    console.log('Element received:', element);

    if (!element) {
        console.error('No element received from This input');
        return;
    }

    if (!element.querySelector('canvas')) {
        const canvasElement = document.createElement('canvas');
        canvasElement.id = 'fabricCanvas';
        canvasElement.width = Script.Inputs.CanvasWidth || 600;
        canvasElement.height = Script.Inputs.CanvasHeight || 160;
        element.appendChild(canvasElement);
        console.log('Canvas element created and appended');
    }

    const canvasElement = element.querySelector('canvas');
    console.log('Canvas element:', canvasElement);

    if (!canvasElement) {
        console.error('Canvas element could not be found');
        return;
    }

    try {
        canvas = new fabric.Canvas(canvasElement);
        console.log('Fabric.js canvas initialized:', canvas);

        // Add rounded corners
        const radius = Script.Inputs.CornerRadius || 20; // default corner radius of 20px
        const rect = new fabric.Rect({
            width: canvas.width,
            height: canvas.height,
            rx: radius,
            ry: radius,
            fill: 'transparent'
        });
        canvas.clipPath = rect;

        // Check if a background color is provided, and only set the default if none is provided
        const defaultBackgroundColor = "#FFE6D5";  // Soft peach
        const bgColor = Script.Inputs.BackgroundColor || defaultBackgroundColor;
        canvas.setBackgroundColor(bgColor, canvas.renderAll.bind(canvas));
        canvas.initialBackgroundColor = bgColor;

    } catch (error) {
        console.error('Failed to initialize Fabric.js canvas:', error);
    }

    // Load the Fredoka font
    loadFont('Fredoka', '/fonts/Fredoka/static/Fredoka-Regular.ttf');

    Script.Outputs.CanvasReady();
};

// Updated signal to add text with specific positioning and alignment
Script.Signals.AddText = function () {
    if (canvas) {
        const textAreaWidth = 400; // 600px - 200px
        const text = new fabric.Textbox(Script.Inputs.Text || 'Editable Text', {
            left: 140,
            top: 50, // You can adjust this value as needed
            width: textAreaWidth,
            fontFamily: Script.Inputs.FontFamily || 'Fredoka, sans-serif', // Set Fredoka as default
            fontSize: Script.Inputs.FontSize || 80,
            fill: Script.Inputs.TextColor || 'black',
            textAlign: 'center',
            originX: 'left',
            originY: 'top',
            // Adding options to make canvas additions static. This should be revised later in future versions
            selectable: false,   // Make the text static
            hasControls: false,  // Disable controls for resizing or rotating
            evented: false       // Disable interaction events
        });

        // Center the text vertically within the canvas
        text.top = (canvas.height - text.height) / 2;

        canvas.add(text);
        canvas.renderAll();
        console.log('Text added to canvas:', text);
    }
};

// Updated signal to add an image from a URL with resizing
Script.Signals.AddImageFromUrl = function () {
    const imageUrl = Script.Inputs.ImageUrl;
    console.log('Adding image from URL:', imageUrl);

    if (canvas && imageUrl) {
        fabric.Image.fromURL(imageUrl, function (img) {
            // Calculate scaling factor to fit height to 200px max
            const scaleFactor = 160 / img.height;

            // Only scale down, not up
            if (scaleFactor < 1) {
                img.scale(scaleFactor);
            }

            canvas.add(img);
            canvas.renderAll();
            console.log('Image added to canvas:', img);
        }, { 
            crossOrigin: 'anonymous',
        });
    }
};

// Updated signal to export the canvas as an image file
Script.Signals.ExportAsImage = function () {
    if (canvas) {
        const imageData = canvas.toDataURL({
            format: 'png',
            quality: 1
        });

        // Convert base64 to blob
        const byteString = atob(imageData.split(',')[1]);
        const mimeString = imageData.split(',')[0].split(':')[1].split(';')[0];
        const ab = new ArrayBuffer(byteString.length);
        const ia = new Uint8Array(ab);
        for (let i = 0; i < byteString.length; i++) {
            ia[i] = byteString.charCodeAt(i);
        }
        const blob = new Blob([ab], {type: mimeString});

        // Create a File object
        const fileName = 'canvas_export.png';
        const file = new File([blob], fileName, {type: 'image/png'});

        Script.Outputs.ImageFile = file;
        Script.Outputs.ImageExported();
        console.log('Canvas exported as image file:', fileName);
    }
};

// New signal to reset the canvas
Script.Signals.ResetCanvas = function () {
    if (canvas) {
        // Clear all objects from the canvas
        canvas.clear();

        // Reapply the background color if it was set
        if (Script.Inputs.BackgroundColor) {
            canvas.setBackgroundColor(Script.Inputs.BackgroundColor, canvas.renderAll.bind(canvas));
        }

        canvas.renderAll();
        console.log('Canvas reset: All objects removed');
    } else {
        console.log('Canvas not initialized, unable to reset');
    }
};

// Cleanup when the node is unmounted
Script.OnDestroy = function () {
    if (canvas) {
        canvas.dispose();
        canvas = null;
        console.log('Canvas disposed');
    }
};

Script.Signals.DeleteSelectedObject = function () {
    if (canvas) {
        const activeObject = canvas.getActiveObject();
        if (activeObject) {
            canvas.remove(activeObject);
            canvas.renderAll();
            console.log('Selected object deleted:', activeObject);
        } else {
            console.log('No object selected to delete');
        }
    }
};

// New signal to set the background color
Script.Signals.SetBackgroundColor = function () {
    if (canvas) {
        const color = Script.Inputs.colour;
        if (color) {
            canvas.setBackgroundColor(color, function () {
                canvas.renderAll();  // Ensure the canvas re-renders after setting the background color
                console.log('Canvas background color set to:', color);
            });
        } else {
            console.log('No color specified for background');
        }
    }
};

