            width: textAreaWidth,
            fontFamily: Script.Inputs.FontFamily || 'Fredoka, sans-serif',
            fontSize: Script.Inputs.FontSize || 40,
            fill: Script.Inputs.TextColor || 'white', // Default to white text
            textAlign: 'center',
            originX: 'left',
            originY: 'top',
            selectable: true,
            hasControls: false,
            evented: true,
            stroke: Script.Inputs.TextStrokeColor || 'black', // Add an outline
            strokeWidth: Script.Inputs.TextStrokeWidth || 1, // Thin stroke by default
            shadow: new fabric.Shadow({
                color: Script.Inputs.TextShadowColor || 'rgba(0,0,0,0.5)',
                blur: Script.Inputs.TextShadowBlur || 5,
                offsetX: Script.Inputs.TextShadowOffsetX || 2,
                offsetY: Script.Inputs.TextShadowOffsetY || 2
            })
        });

        // Center the text vertically within the canvas
        //text.top = (canvas.height - text.height) / 2;

        canvas.add(text);
        canvas.renderAll();
    }
};

// Global object to track added images
let addedImages = {};

Script.Signals.AddImageFromUrl = function () {
    const imageUrl = Script.Inputs.ImageUrl;
    const layerIndex = Script.Inputs.LayerIndex || 0;

    if (canvas && imageUrl) {
        // Check if this image URL has already been added
        if (addedImages[imageUrl]) {
            console.log('Image already added, skipping');
            return;
        }

        fabric.Image.fromURL(imageUrl, function (img) {
            // Double-check to prevent race conditions
            if (addedImages[imageUrl]) {
                console.log('Image already added (race condition), skipping');
                return;
            }

            // Calculate scaling factor to fit height to 200px max
            const scaleFactor = 160 / img.height;

            // Only scale down, not up
            if (scaleFactor < 1) {
                img.scale(scaleFactor);
            }

            // Set properties to allow moving but not resizing
            img.set({
                selectable: true,
                hasControls: false,
                evented: true
            });

            // Generate a unique id for this image
            const uniqueId = 'img_' + Date.now();
            img.set('id', uniqueId);

            // Insert the image at the specified layer index
            canvas.insertAt(img, layerIndex);
            canvas.renderAll();

            // Mark this image URL as added
            addedImages[imageUrl] = uniqueId;

            console.log('Image added successfully:');
        }, { 
            crossOrigin: 'anonymous',
        });
    }
};

Script.Signals.addBackground = function () {
    const imageUrl = Script.Inputs.backgroundUrl;
    const layerIndex = 0; 

    if (canvas && imageUrl) {
        fabric.Image.fromURL(imageUrl, function (img) {
            // Calculate scaling factor to fit height to 200px max
            const scaleFactor = 160 / img.height;

            // Only scale down, not up
            if (scaleFactor < 1) {
                img.scale(scaleFactor);
            }

            // Set properties to allow moving but not resizing
            img.set({
                selectable: false,
                hasControls: false,
                evented: true
            });

            // Insert the image at the specified layer index
            canvas.insertAt(img, layerIndex);
            canvas.renderAll();
        }, { 
            crossOrigin: 'anonymous',
        });
    }
};

// Updated signal to export the canvas as an image file
Script.Signals.ExportAsImage = function () {
    if (canvas) {
        const imageData = canvas.toDataURL({
            format: 'png',
            quality: 1
        });

        // Convert base64 to blob
        const byteString = atob(imageData.split(',')[1]);
        const mimeString = imageData.split(',')[0].split(':')[1].split(';')[0];
        const ab = new ArrayBuffer(byteString.length);
        const ia = new Uint8Array(ab);
        for (let i = 0; i < byteString.length; i++) {
            ia[i] = byteString.charCodeAt(i);
        }
        const blob = new Blob([ab], {type: mimeString});

        // Create a File object
        const fileName = 'canvas_export.png';
        const file = new File([blob], fileName, {type: 'image/png'});

        Script.Outputs.ImageFile = file;
        Script.Outputs.ImageExported();
    }
};

// Update ResetCanvas function to clear the addedImages tracker
Script.Signals.ResetCanvas = function () {
    if (canvas) {
        // Clear all objects from the canvas
        canvas.clear();

        // Reset the clip path (rounded corners)
        const radius = Script.Inputs.CornerRadius || 20;
        const rect = new fabric.Rect({
            width: canvas.width,
            height: canvas.height,
            rx: radius,
            ry: radius,
            fill: 'transparent'
        });
        canvas.clipPath = rect;

        // Reapply the initial background color
        const bgColor = canvas.initialBackgroundColor || Script.Inputs.BackgroundColor || "#FFE6D5";
        canvas.setBackgroundColor(bgColor, canvas.renderAll.bind(canvas));

        // Clear the addedImages tracker
        addedImages = {};

        console.log('Canvas reset completed');
        canvas.renderAll();
    } else {
        console.log('Canvas not initialized, unable to reset');
    }
};

// Cleanup when the node is unmounted
Script.OnDestroy = function () {
    if (canvas) {
        canvas.dispose();
        canvas = null;
    }
};

Script.Signals.DeleteSelectedObject = function () {
    if (canvas) {
        const activeObject = canvas.getActiveObject();
        if (activeObject) {
            canvas.remove(activeObject);
            canvas.renderAll();
        } else {
            console.log('No object selected to delete');
        }
    }
};

// New signal to set the background color
Script.Signals.SetBackgroundColor = function () {
    if (canvas) {
        const color = Script.Inputs.colour;
        if (color) {
            canvas.setBackgroundColor(color, canvas.renderAll.bind(canvas));
        } else {
            console.log('No color specified for background');
        }
    }
};
