Script.Signals.Setup = function () {
    const element = Script.Inputs.This.getDOMElement();
    console.log('Element received:', element);

    if (!element) {
        console.error('No element received from This input');
        return;
    }

    if (!element.querySelector('canvas')) {
        const canvasElement = document.createElement('canvas');
        canvasElement.id = 'fabricCanvas';
        canvasElement.width = Script.Inputs.CanvasWidth || 600;
        canvasElement.height = Script.Inputs.CanvasHeight || 160;
        element.appendChild(canvasElement);
