    }

    const canvasElement = element.querySelector('canvas');
    console.log('Canvas element:', canvasElement);

    if (!canvasElement) {
        console.error('Canvas element could not be found');
        return;
    }

    try {
        canvas = new fabric.Canvas(canvasElement);
        console.log('Fabric.js canvas initialized:', canvas);

        // Add rounded corners
        const radius = Script.Inputs.CornerRadius || 20; // default corner radius of 20px
        const rect = new fabric.Rect({
            width: canvas.width,
            height: canvas.height,
            rx: radius,
            ry: radius,
            fill: 'transparent'
        });
        canvas.clipPath = rect;

        // Check if a background color is provided, and only set the default if none is provided
        const defaultBackgroundColor = "#FFE6D5";  // Soft peach
        const bgColor = Script.Inputs.BackgroundColor || defaultBackgroundColor;
        canvas.setBackgroundColor(bgColor, canvas.renderAll.bind(canvas));
        canvas.initialBackgroundColor = bgColor;

    } catch (error) {
        console.error('Failed to initialize Fabric.js canvas:', error);
    }

    // Load the Fredoka font
    loadFont('Fredoka', '/fonts/Fredoka/static/Fredoka-Regular.ttf');

    Script.Outputs.CanvasReady();
};

// Updated signal to add text with specific positioning and alignment
Script.Signals.AddText = function () {
    if (canvas) {
        const textAreaWidth = 400; // 600px - 200px
        const text = new fabric.Textbox(Script.Inputs.Text || 'Editable Text', {
            left: 140,
            top: 50, // You can adjust this value as needed
            width: textAreaWidth,
            fontFamily: Script.Inputs.FontFamily || 'Fredoka, sans-serif', // Set Fredoka as default
            fontSize: Script.Inputs.FontSize || 80,
            fill: Script.Inputs.TextColor || 'black',
            textAlign: 'center',
            originX: 'left',
            originY: 'top',
            // Adding options to make canvas additions static. This should be revised later in future versions
            selectable: false,   // Make the text static
            hasControls: false,  // Disable controls for resizing or rotating
            evented: false       // Disable interaction events
        });

        // Center the text vertically within the canvas
        text.top = (canvas.height - text.height) / 2;

        canvas.add(text);
        canvas.renderAll();
        console.log('Text added to canvas:', text);
    }
};

// Updated signal to add an image from a URL with resizing
Script.Signals.AddImageFromUrl = function () {
    const imageUrl = Script.Inputs.ImageUrl;
    console.log('Adding image from URL:', imageUrl);

    if (canvas && imageUrl) {
        fabric.Image.fromURL(imageUrl, function (img) {
            // Calculate scaling factor to fit height to 200px max
            const scaleFactor = 160 / img.height;

            // Only scale down, not up
            if (scaleFactor < 1) {
                img.scale(scaleFactor);
            }

            canvas.add(img);
            canvas.renderAll();
            console.log('Image added to canvas:', img);
        }, { 
            crossOrigin: 'anonymous',
        });
    }
};

